import{u as i,o as l,c as d,b as t,f as m,a as e,d as a,e as o}from"./chunks/framework.f8ef6e90.js";import{B as n}from"./chunks/ButtonActionSet.ea62f3d7.js";const s=e("div",{class:"markdown_body"},null,-1),x=e("p",{class:"mt-[18px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.5] paragraph_spacing"},[a("Jesteśmy software housem z Krakowa. Założycielem jest "),e("a",{href:"https://www.linkedin.com/in/konradotrebski/",class:"text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors",target:"_blank",rel:"noreferrer"},"Konrad Otrębski"),a(", developer, prelegent na konferencjach, wieloletni praktyk Test-Driven Development oraz DevOps’owego flow. "),e("strong",null,"Nie pracujemy na branchach, wszystko pushujemy na master i robimy po 50-100 deploymentów na produkcję miesięcznie (na osobę)"),a(". Pracujemy ze stabilnymi oraz świadomymi klientami przy długoterminowych projektach.")],-1),p=e("p",{class:"mt-[18px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.5] paragraph_spacing"},"Klientem, dla którego odbywa się projekt jest polski producent odzieży. Firma produkuje dedykowaną odzież dla klientów b2b (np. kurierów, kelnerów, sportowców, hoteli itd.), w tym wielu najbardziej znanych marek w Polsce. Celem projektu jest stabilizacja systemu, który zarządza całą fabryką klienta, a następnie rozbudowa jego możliwości. Naszą podstawową metodą pracy jest TDD.",-1),c=o('<h2 id="wymagania" tabindex="-1" class="mb-[28px] text-color1 font-semibold text-[24px] md:text-[28px] leading-normal">Wymagania <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#wymagania" aria-label="Permalink to &quot;Wymagania&quot;">​</a></h2><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Java - bardzo dobra znajomość,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Test-Driven Development - znasz, posiadasz doświadczenie i chcesz stosować TDD na co dzień,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Refactoring, Clean Code - potrafisz pisać dobrej jakości kod, a także modyfikować już istniejący,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Nie wymagamy znajomości Domain-Driven Design (natomiast pracujemy w tym podejściu),</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Język polski,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Pasja do programowania poparta Twoim ciągłym rozwojem.</li></ul><h2 id="zadania" tabindex="-1" class="mb-[28px] text-color1 font-semibold text-[24px] md:text-[28px] leading-normal">Zadania <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#zadania" aria-label="Permalink to &quot;Zadania&quot;">​</a></h2><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Praca w TDD (naprawdę najpierw piszemy test),</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Zadania zarówno greenfieldowe jak i refactoring/bug fixing, w proporcjach około 50/50,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Code reviews dla innych członków zespołu oraz pair programming.</li></ul><h2 id="oferta" tabindex="-1" class="mb-[28px] text-color1 font-semibold text-[24px] md:text-[28px] leading-normal">Oferta <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#oferta" aria-label="Permalink to &quot;Oferta&quot;">​</a></h2><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Udział w projekcie, w którym ratujemy aplikację legacy i przekształcimy ją w sprawnie funkcjonujący i nowoczesny system, który zarządza całą fabryką klienta,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><strong>Trunk-Based Development</strong> - praca w unikatowym DevOpsowym flow - brak branchy, brak merge conflictów, dostarczamy szybciej niż konkurencja w przyjemnych dla programisty warunkach, <strong>nauczysz się pracować w tym podejściu</strong>, osobiście będziesz wykonywać ok. 50-100 deploymentów na produkcję miesięcznie,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><strong>Nielimitowana biblioteczka</strong> - nielimitowany budżet na książki papierowe, ebooki, audiobooki czy kursy (typu Udemy, Coursera),</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><strong>Praca ze świadomym i pozytywnie nastawionym klientem</strong> - któremu mocno zależy na jakości oprogramowania,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><strong>Wynagrodzenie w zakresie 20-25k PLN netto</strong> - umowa B2B w pełnym wymiarze czasu,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Praca w 100% zdalna - zjazdy firmowe raz na kwartał,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">26 dni urlopu oraz wolne święta państwowe,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">4 dni dodatkowego wolnego na konferencje/naukę,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Sprzęt wg. Twoich życzeń (z tym, że pracujemy na Linux Ubuntu).</li></ul><h2 id="etapy-rekrutacji" tabindex="-1" class="mb-[28px] text-color1 font-semibold text-[24px] md:text-[28px] leading-normal">Etapy rekrutacji <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#etapy-rekrutacji" aria-label="Permalink to &quot;Etapy rekrutacji&quot;">​</a></h2><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Formularz aplikacyjny (wystarczy jedno: LinkedIn lub CV),</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Wstępna rozmowa telefoniczna - ok. 10-30 min z <a href="https://www.linkedin.com/in/konradotrebski/" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Konradem</a>, naszym tech-leadem,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Pair coding online - ok. 1-2h, aby ustalić jak myślisz i pracujesz z kodem,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Rozmowa końcowa - ok. 1h.</li></ul><h2 id="zasady-rekrutacji" tabindex="-1" class="mb-[28px] text-color1 font-semibold text-[24px] md:text-[28px] leading-normal">Zasady rekrutacji <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#zasady-rekrutacji" aria-label="Permalink to &quot;Zasady rekrutacji&quot;">​</a></h2><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Po każdym etapie udzielamy Ci natychmiastowego i szczegółowego feedbacku (na koniec rozmowy),</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><strong>Odpowiadamy każdemu!</strong> Na pierwszą rozmowę możesz umówić się samodzielnie zaraz po zaaplikowaniu (przy użyciu naszego kalendarza).</li></ul>',10),g=o('<h2 id="wiecej-informacji" tabindex="-1" class="mb-[28px] text-color1 font-semibold text-[24px] md:text-[28px] leading-normal">Więcej informacji <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#wiecej-informacji" aria-label="Permalink to &quot;Więcej informacji&quot;">​</a></h2><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="mailto:info@p3doweks.com" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">info@p3doweks.com</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="https://www.linkedin.com/company/p3doweks/" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Nasz profil na LinkedIn</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="tel:+48796594347" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">+48 796 594 347</a></li></ul>',2),h=JS