import{_ as e,o as a,c as t,e as o}from"./chunks/framework.f8ef6e90.js";const w=JSON.parse('{"title":"Polityka prywatności","description":"Polityka prywatności","frontmatter":{"layout":"landing-page","title":"Polityka prywatności","description":"Polityka prywatności","language":"PL"},"headers":[],"relativePath":"legal/polityka-prywatnosci.md","filePath":"legal/polityka-prywatnosci.md"}'),i={name:"legal/polityka-prywatnosci.md"},n=o('<h1 id="polityka-prywatnosci-opisuje-zasady-przetwarzania-przez-nas-informacji-na-twoj-temat-w-tym-danych-osobowych-oraz-ciasteczek-czyli-tzw-cookies" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[36px] md:text-[48px] xl:text-[64px] leading-[1.2]">Polityka prywatności opisuje zasady przetwarzania przez nas informacji na Twój temat, w tym danych osobowych oraz ciasteczek, czyli tzw. cookies. <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#polityka-prywatnosci-opisuje-zasady-przetwarzania-przez-nas-informacji-na-twoj-temat-w-tym-danych-osobowych-oraz-ciasteczek-czyli-tzw-cookies" aria-label="Permalink to &quot;Polityka prywatności opisuje zasady przetwarzania przez nas informacji na Twój temat, w tym danych osobowych oraz ciasteczek, czyli tzw. cookies.&quot;">​</a></h1><hr><h2 id="_1-informacje-ogolne" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>1. Informacje ogólne</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_1-informacje-ogolne" aria-label="Permalink to &quot;**1\\. Informacje ogólne**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Niniejsza polityka dotyczy Serwisu www, funkcjonującego pod adresem url: <strong>p3doweks.com</strong></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Operatorem serwisu oraz Administratorem danych osobowych jest: Przedewsieńska Pracownia Informatyczna Zlevix Ilościowiec Dyplomowany Konrad Otrębski Gwardzistów 132, 43-607 Jaworzno</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Adres kontaktowy poczty elektronicznej operatora: <a href="mailto:info@p3doweks.com" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">info@p3doweks.com</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Operator jest Administratorem Twoich danych osobowych w odniesieniu do danych podanych dobrowolnie w Serwisie.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Serwis wykorzystuje dane osobowe w następujących celach:</li></ol><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Prowadzenie newslettera</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Obsługa zapytań przez formularz</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Realizacja zamówionych usług</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Prezentacja oferty lub informacji</li></ul><ol start="7" class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Serwis realizuje funkcje pozyskiwania informacji o użytkownikach i ich zachowaniu w następujący sposób: <ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Poprzez dobrowolnie wprowadzone w formularzach dane, które zostają wprowadzone do systemów Operatora.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Poprzez zapisywanie w urządzeniach końcowych plików cookie (tzw. „ciasteczka”).</li></ol></li></ol><h2 id="_2-wybrane-metody-ochrony-danych-stosowane-przez-operatora" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>2. Wybrane metody ochrony danych stosowane przez Operatora</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_2-wybrane-metody-ochrony-danych-stosowane-przez-operatora" aria-label="Permalink to &quot;**2\\. Wybrane metody ochrony danych stosowane przez Operatora**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Miejsca logowania i wprowadzania danych osobowych są chronione w warstwie transmisji (certyfikat SSL). Dzięki temu dane osobowe i dane logowania, wprowadzone na stronie, zostają zaszyfrowane w komputerze użytkownika i mogą być odczytane jedynie na docelowym serwerze.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Hasła użytkowników są przechowywane w postaci hashowanej. Funkcja hashująca działa jednokierunkowo - nie jest możliwe odwrócenie jej działania, co stanowi obecnie współczesny standard w zakresie przechowywania haseł użytkowników.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Operator okresowo zmienia swoje hasła administracyjne.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">W celu ochrony danych Operator regularnie wykonuje kopie bezpieczeństwa.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Istotnym elementem ochrony danych jest regularna aktualizacja wszelkiego oprogramowania, wykorzystywanego przez Operatora do przetwarzania danych osobowych, co w szczególności oznacza regularne aktualizacje komponentów programistycznych.</li></ol><h2 id="_3-hosting" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>3. Hosting</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_3-hosting" aria-label="Permalink to &quot;**3\\. Hosting**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Serwis jest hostowany (technicznie utrzymywany) na serwerach operatora: inna firma</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Firma hostingowa w celu zapewnienia niezawodności technicznej prowadzi logi na poziomie serwera. Zapisowi mogą podlegać: <ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">zasoby określone identyfikatorem URL (adresy żądanych zasobów – stron, plików),</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">czas nadejścia zapytania,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">czas wysłania odpowiedzi,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">nazwę stacji klienta – identyfikacja realizowana przez protokół HTTP,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">informacje o błędach jakie nastąpiły przy realizacji transakcji HTTP,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">adres URL strony poprzednio odwiedzanej przez użytkownika (referer link) – w przypadku gdy przejście do Serwisu nastąpiło przez odnośnik,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">informacje o przeglądarce użytkownika,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">informacje o adresie IP,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">informacje diagnostyczne związane z procesem samodzielnego zamawiania usług poprzez rejestratory na stronie,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">informacje związane z obsługą poczty elektronicznej kierowanej do Operatora oraz wysyłanej przez Operatora.</li></ul></li></ol><h2 id="_4-twoje-prawa-i-dodatkowe-informacje-o-sposobie-wykorzystania-danych" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>4. Twoje prawa i dodatkowe informacje o sposobie wykorzystania danych</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_4-twoje-prawa-i-dodatkowe-informacje-o-sposobie-wykorzystania-danych" aria-label="Permalink to &quot;**4\\. Twoje prawa i dodatkowe informacje o sposobie wykorzystania danych**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">W niektórych sytuacjach Administrator ma prawo przekazywać Twoje dane osobowe innym odbiorcom, jeśli będzie to niezbędne do wykonania zawartej z Tobą umowy lub do zrealizowania obowiązków ciążących na Administratorze. Dotyczy to takich grup odbiorców: <ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">firma hostingowa na zasadzie powierzenia</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">upoważnieni pracownicy i współpracownicy, którzy korzystają z danych w celu realizacji celu działania strony</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">firmy, świadczące usługi marketingu na rzecz Administratora</li></ul></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Twoje dane osobowe przetwarzane przez Administratora nie dłużej, niż jest to konieczne do wykonania związanych z nimi czynności określonych osobnymi przepisami (np. o prowadzeniu rachunkowości). W odniesieniu do danych marketingowych dane nie będą przetwarzane dłużej niż przez 3 lata.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Przysługuje Ci prawo żądania od Administratora: <ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">dostępu do danych osobowych Ciebie dotyczących,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">ich sprostowania,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">usunięcia,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">ograniczenia przetwarzania,</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">oraz przenoszenia danych.</li></ul></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Przysługuje Ci prawo do złożenia sprzeciwu w zakresie przetwarzania wskazanego w pkt 3.2 wobec przetwarzania danych osobowych w celu wykonania prawnie uzasadnionych interesów realizowanych przez Administratora, w tym profilowania, przy czym prawo sprzeciwu nie będzie mogło być wykonane w przypadku istnienia ważnych prawnie uzasadnionych podstaw do przetwarzania, nadrzędnych wobec Ciebie interesów, praw i wolności, w szczególności ustalenia, dochodzenia lub obrony roszczeń.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Na działania Administratora przysługuje skarga do Prezesa Urzędu Ochrony Danych Osobowych, ul. Stawki 2, 00-193 Warszawa.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Podanie danych osobowych jest dobrowolne, lecz niezbędne do obsługi Serwisu.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">W stosunku do Ciebie mogą być podejmowane czynności polegające na zautomatyzowanym podejmowaniu decyzji, w tym profilowaniu w celu świadczenia usług w ramach zawartej umowy oraz w celu prowadzenia przez Administratora marketingu bezpośredniego.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Dane osobowe są przekazywane od krajów trzecich w rozumieniu przepisów o ochronie danych osobowych. Oznacza to, że przesyłamy je poza teren Unii Europejskiej.</li></ol><h2 id="_5-informacje-w-formularzach" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>5. Informacje w formularzach</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_5-informacje-w-formularzach" aria-label="Permalink to &quot;**5\\. Informacje w formularzach**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Serwis zbiera informacje podane dobrowolnie przez użytkownika, w tym dane osobowe, o ile zostaną one podane.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Serwis może zapisać informacje o parametrach połączenia (oznaczenie czasu, adres IP).</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Serwis, w niektórych wypadkach, może zapisać informację ułatwiającą powiązanie danych w formularzu z adresem e-mail użytkownika wypełniającego formularz. W takim wypadku adres e-mail użytkownika pojawia się wewnątrz adresu url strony zawierającej formularz.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Dane podane w formularzu są przetwarzane w celu wynikającym z funkcji konkretnego formularza, np. w celu dokonania procesu obsługi zgłoszenia serwisowego lub kontaktu handlowego, rejestracji usług itp. Każdorazowo kontekst i opis formularza w czytelny sposób informuje, do czego on służy.</li></ol><h2 id="_6-logi-administratora" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>6. Logi Administratora</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_6-logi-administratora" aria-label="Permalink to &quot;**6\\. Logi Administratora**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Informacje zachowaniu użytkowników w serwisie mogą podlegać logowaniu. Dane te są wykorzystywane w celu administrowania serwisem.</li></ol><h2 id="_7-istotne-techniki-marketingowe" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>7. Istotne techniki marketingowe</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_7-istotne-techniki-marketingowe" aria-label="Permalink to &quot;**7\\. Istotne techniki marketingowe**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Operator stosuje analizę statystyczną ruchu na stronie, poprzez Google Analytics (Google Inc. z siedzibą w USA). Operator nie przekazuje do operatora tej usługi danych osobowych, a jedynie zanonimizowane informacje. Usługa bazuje na wykorzystaniu ciasteczek w urządzeniu końcowym użytkownika. W zakresie informacji o preferencjach użytkownika gromadzonych przez sieć reklamową Google użytkownik może przeglądać i edytować informacje wynikające z plików cookies przy pomocy narzędzia: <a href="https://www.google.com/ads/preferences/" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">https://www.google.com/ads/preferences/</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Operator stosuje rozwiązanie badające zachowanie użytkowników poprzez tworzenie map ciepła oraz nagrywanie zachowania na stronie. Te informacje są anonimizowane zanim zostaną przesłane do operatora usługi tak, że nie wie on jakiej osoby fizycznej one dotyczą. W szczególności nagrywaniu nie podlegają wpisywane hasła oraz inne dane osobowe.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Operator stosuje rozwiązanie automatyzujące działanie Serwisu w odniesieniu do użytkowników, np. mogące przesłać maila do użytkownika po odwiedzeniu konkretnej podstrony, o ile wyraził on zgodę na otrzymywanie korespondencji handlowej od Operatora.</li></ol><h2 id="_8-informacja-o-plikach-cookies" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>8. Informacja o plikach cookies</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_8-informacja-o-plikach-cookies" aria-label="Permalink to &quot;**8\\. Informacja o plikach cookies**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Serwis korzysta z plików cookies.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Pliki cookies (tzw. „ciasteczka”) stanowią dane informatyczne, w szczególności pliki tekstowe, które przechowywane są w urządzeniu końcowym Użytkownika Serwisu i przeznaczone są do korzystania ze stron internetowych Serwisu. Cookies zazwyczaj zawierają nazwę strony internetowej, z której pochodzą, czas przechowywania ich na urządzeniu końcowym oraz unikalny numer.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Podmiotem zamieszczającym na urządzeniu końcowym Użytkownika Serwisu pliki cookies oraz uzyskującym do nich dostęp jest operator Serwisu.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Pliki cookies wykorzystywane są w następujących celach: <ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">utrzymanie sesji użytkownika Serwisu (po zalogowaniu), dzięki której użytkownik nie musi na każdej podstronie Serwisu ponownie wpisywać loginu i hasła;</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">realizacji celów określonych powyżej w części &quot;Istotne techniki marketingowe&quot;;</li></ol></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">W ramach Serwisu stosowane są dwa zasadnicze rodzaje plików cookies: „sesyjne” (session cookies) oraz „stałe” (persistent cookies). Cookies „sesyjne” są plikami tymczasowymi, które przechowywane są w urządzeniu końcowym Użytkownika do czasu wylogowania, opuszczenia strony internetowej lub wyłączenia oprogramowania (przeglądarki internetowej). „Stałe” pliki cookies przechowywane są w urządzeniu końcowym Użytkownika przez czas określony w parametrach plików cookies lub do czasu ich usunięcia przez Użytkownika.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Oprogramowanie do przeglądania stron internetowych (przeglądarka internetowa) zazwyczaj domyślnie dopuszcza przechowywanie plików cookies w urządzeniu końcowym Użytkownika. Użytkownicy Serwisu mogą dokonać zmiany ustawień w tym zakresie. Przeglądarka internetowa umożliwia usunięcie plików cookies. Możliwe jest także automatyczne blokowanie plików cookies Szczegółowe informacje na ten temat zawiera pomoc lub dokumentacja przeglądarki internetowej.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Ograniczenia stosowania plików cookies mogą wpłynąć na niektóre funkcjonalności dostępne na stronach internetowych Serwisu.</li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]">Pliki cookies zamieszczane w urządzeniu końcowym Użytkownika Serwisu wykorzystywane mogą być również przez współpracujące z operatorem Serwisu podmioty, w szczególności dotyczy to firm: Google (Google Inc. z siedzibą w USA), Facebook (Facebook Inc. z siedzibą w USA), Twitter (Twitter Inc. z siedzibą w USA).</li></ol><h2 id="_9-zarzadzanie-plikami-cookies-–-jak-w-praktyce-wyrazac-i-cofac-zgode" tabindex="-1" class="mb-[24px] text-color1 font-semibold text-[30px] md:text-[36px] xl:text-[48px] leading-normal"><strong>9. Zarządzanie plikami cookies – jak w praktyce wyrażać i cofać zgodę?</strong> <a class="header-anchor text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" href="#_9-zarzadzanie-plikami-cookies-–-jak-w-praktyce-wyrazac-i-cofac-zgode" aria-label="Permalink to &quot;**9\\. Zarządzanie plikami cookies – jak w praktyce wyrażać i cofać zgodę?**&quot;">​</a></h2><ol class="mt-[18px] list-decimal flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><p class="mt-[18px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.5] landing_paragraph_spacing">Jeśli użytkownik nie chce otrzymywać plików cookies, może zmienić ustawienia przeglądarki. Zastrzegamy, że wyłączenie obsługi plików cookies niezbędnych dla procesów uwierzytelniania, bezpieczeństwa, utrzymania preferencji użytkownika może utrudnić, a w skrajnych przypadkach może uniemożliwić korzystanie ze stron www</p></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><p class="mt-[18px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.5] landing_paragraph_spacing">W celu zarządzania ustawienia cookies wybierz z listy poniżej przeglądarkę internetową, której używasz i postępuj zgodnie z instrukcjami:</p><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="https://support.microsoft.com/pl-pl/help/10607/microsoft-edge-view-delete-browser-history" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Edge</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="https://support.microsoft.com/pl-pl/help/278835/how-to-delete-cookie-files-in-internet-explorer" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Internet Explorer</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="http://support.google.com/chrome/bin/answer.py?hl=pl&amp;answer=95647" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Chrome</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="http://support.apple.com/kb/PH5042" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Safari</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="http://support.mozilla.org/pl/kb/W%C5%82%C4%85czanie%20i%20wy%C5%82%C4%85czanie%20obs%C5%82ugi%20ciasteczek" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Firefox</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="http://help.opera.com/Windows/12.10/pl/cookies.html" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Opera</a></li></ul><p class="mt-[18px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.5] landing_paragraph_spacing">Urządzenia mobilne:</p><ul class="mt-[18px] list-disc flex flex-col justify-start gap-[12px] landing_paragraph_spacing"><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="http://support.google.com/chrome/bin/answer.py?hl=pl&amp;answer=95647" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Android</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="http://support.apple.com/kb/HT1677?viewlocale=pl_PL" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Safari (iOS)</a></li><li class="ml-[25px] text-gray1 text-[16px] md:text-[18px] font-normal leading-[1.75] md:leading-[1.5]"><a href="http://www.windowsphone.com/pl-pl/how-to/wp7/web/changing-privacy-and-other-browser-settings" class="text-red1 text-[16px] md:text-[18px] font-medium leading-normal underline hover:text-red2 transition-colors" target="_blank" rel="noreferrer">Windows Phone</a></li></ul></li></ol>',22),r=[n];function l(s,d,p,x,m,c){return a(),t("div",null,r)}const z=e(i,[["render",l]]);export{w as __pageData,z as default};
